import { useQuery, useMutation } from "@tanstack/react-query";
import { useRoute, Link } from "wouter";
import { AdminLayout } from "@/components/admin/AdminLayout";
import { OrderStatusBadge, getStatusOptions } from "@/components/admin/OrderStatusBadge";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Separator } from "@/components/ui/separator";
import { Skeleton } from "@/components/ui/skeleton";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { ArrowRight, MapPin, Phone, Mail, CreditCard } from "lucide-react";
import type { OrderWithItems } from "@shared/schema";
import { format } from "date-fns";
import { ar } from "date-fns/locale";

export default function AdminOrderDetails() {
  const [, params] = useRoute("/admin/orders/:id");
  const orderId = params?.id;
  const { toast } = useToast();

  const { data: order, isLoading } = useQuery<OrderWithItems>({
    queryKey: ["/api/orders", orderId],
    enabled: !!orderId,
  });

  const updateStatusMutation = useMutation({
    mutationFn: async ({ id, status }: { id: number; status: string }) => {
      return await apiRequest("PATCH", `/api/orders/${id}/status`, { status });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/orders", orderId] });
      queryClient.invalidateQueries({ queryKey: ["/api/orders"] });
      toast({ title: "تم تحديث حالة الطلب" });
    },
    onError: () => {
      toast({ title: "حدث خطأ", variant: "destructive" });
    },
  });

  const statusOptions = getStatusOptions();

  if (isLoading) {
    return (
      <AdminLayout title="تفاصيل الطلب">
        <div className="space-y-6">
          <Skeleton className="h-8 w-48" />
          <Skeleton className="h-64 w-full" />
        </div>
      </AdminLayout>
    );
  }

  if (!order) {
    return (
      <AdminLayout title="تفاصيل الطلب">
        <div className="text-center py-16">
          <p className="text-muted-foreground mb-4">الطلب غير موجود</p>
          <Link href="/admin/orders">
            <Button>العودة للطلبات</Button>
          </Link>
        </div>
      </AdminLayout>
    );
  }

  const paymentMethodLabel = order.paymentMethod === "cod" ? "الدفع عند الاستلام" : "بطاقة ائتمان";

  return (
    <AdminLayout
      title={`طلب #${order.orderNumber}`}
      description={
        order.createdAt
          ? format(new Date(order.createdAt), "dd MMMM yyyy - HH:mm", { locale: ar })
          : undefined
      }
      actions={
        <Link href="/admin/orders">
          <Button variant="outline" className="gap-2">
            <ArrowRight className="h-4 w-4" />
            العودة للطلبات
          </Button>
        </Link>
      }
    >
      <div className="grid lg:grid-cols-3 gap-6">
        <div className="lg:col-span-2 space-y-6">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between gap-4 flex-wrap">
              <CardTitle>حالة الطلب</CardTitle>
              <Select
                value={order.status || "pending"}
                onValueChange={(status) =>
                  updateStatusMutation.mutate({ id: order.id, status })
                }
              >
                <SelectTrigger className="w-40" data-testid="select-update-status">
                  <SelectValue>
                    <OrderStatusBadge status={order.status || "pending"} />
                  </SelectValue>
                </SelectTrigger>
                <SelectContent>
                  {statusOptions.map((option) => (
                    <SelectItem key={option.value} value={option.value}>
                      {option.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </CardHeader>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>المنتجات</CardTitle>
            </CardHeader>
            <CardContent>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead className="text-right">المنتج</TableHead>
                    <TableHead className="text-right">السعر</TableHead>
                    <TableHead className="text-right">الكمية</TableHead>
                    <TableHead className="text-right">الإجمالي</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {order.items?.map((item) => (
                    <TableRow key={item.id}>
                      <TableCell className="font-medium">{item.productNameAr}</TableCell>
                      <TableCell>{parseFloat(item.price).toFixed(2)} ر.س</TableCell>
                      <TableCell>{item.quantity}</TableCell>
                      <TableCell>{parseFloat(item.total).toFixed(2)} ر.س</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>

              <Separator className="my-4" />

              <div className="space-y-2">
                <div className="flex justify-between">
                  <span className="text-muted-foreground">المجموع الفرعي</span>
                  <span>{parseFloat(order.subtotal).toFixed(2)} ر.س</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-muted-foreground">الشحن</span>
                  <span>
                    {parseFloat(order.shippingCost || "0") === 0 ? (
                      <span className="text-green-600">مجاني</span>
                    ) : (
                      `${parseFloat(order.shippingCost || "0").toFixed(2)} ر.س`
                    )}
                  </span>
                </div>
                <Separator />
                <div className="flex justify-between font-bold text-lg">
                  <span>الإجمالي</span>
                  <span className="text-primary" data-testid="text-order-total">
                    {parseFloat(order.total).toFixed(2)} ر.س
                  </span>
                </div>
              </div>
            </CardContent>
          </Card>

          {order.notes && (
            <Card>
              <CardHeader>
                <CardTitle>ملاحظات</CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-muted-foreground">{order.notes}</p>
              </CardContent>
            </Card>
          )}
        </div>

        <div className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>معلومات العميل</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <p className="font-medium" data-testid="text-customer-name">{order.customerName}</p>
              </div>
              <div className="flex items-center gap-2 text-muted-foreground">
                <Mail className="h-4 w-4" />
                <span dir="ltr">{order.customerEmail}</span>
              </div>
              {order.customerPhone && (
                <div className="flex items-center gap-2 text-muted-foreground">
                  <Phone className="h-4 w-4" />
                  <span dir="ltr">{order.customerPhone}</span>
                </div>
              )}
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>عنوان الشحن</CardTitle>
            </CardHeader>
            <CardContent className="space-y-2">
              <div className="flex items-start gap-2">
                <MapPin className="h-4 w-4 mt-1 text-muted-foreground" />
                <div>
                  {order.shippingCity && (
                    <p className="font-medium">{order.shippingCity}</p>
                  )}
                  <p className="text-muted-foreground">{order.shippingAddress}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>طريقة الدفع</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-center gap-2">
                <CreditCard className="h-4 w-4 text-muted-foreground" />
                <span>{paymentMethodLabel}</span>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </AdminLayout>
  );
}

client/src/components/admin/OrderStatusB.content:
import { Badge } from "@/components/ui/badge";

type OrderStatus = "pending" | "processing" | "shipped" | "delivered" | "cancelled";

interface OrderStatusBadgeProps {
  status: string;
}

const statusConfig: Record<OrderStatus, { label: string; className: string }> = {
  pending: { label: "قيد الانتظار", className: "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200" },
  processing: { label: "قيد التجهيز", className: "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200" },
  shipped: { label: "تم الشحن", className: "bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200" },
  delivered: { label: "تم التسليم", className: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200" },
  cancelled: { label: "ملغي", className: "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200" },
};

export function OrderStatusBadge({ status }: OrderStatusBadgeProps) {
  const config = statusConfig[status as OrderStatus] || statusConfig.pending;

  return (
    <Badge variant="outline" className={config.className}>
      {config.label}
    </Badge>
  );
}

export function getStatusOptions() {
  return Object.entries(statusConfig).map(([value, { label }]) => ({
    value,
    label,
  }));
}

client/src/pages/MyAccount.tsx.content:
import { useEffect, useState } from "react";
import { useLocation } from "wouter";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Header } from "@/components/storefront/Header";
import { Footer } from "@/components/storefront/Footer";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Skeleton } from "@/components/ui/skeleton";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Mail, Phone, MapPin, User, LogOut, Lock, LogIn, Loader2 } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { Customer } from "@shared/schema";

export default function MyAccount() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [customer, setCustomer] = useState<Partial<Customer> | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    address: "",
    city: "",
  });

  // Admin login states
  const [adminUsername, setAdminUsername] = useState("");
  const [adminPassword, setAdminPassword] = useState("");

  useEffect(() => {
    const email = localStorage.getItem("customerEmail");
    if (!email) {
      setLocation("/");
      return;
    }

    const fetchCustomer = async () => {
      try {
        setIsLoading(true);
        const response = await fetch(`/api/customers/by-email?email=${encodeURIComponent(email)}`);
        if (!response.ok) {
          throw new Error("Failed to fetch customer");
        }
        const data = await response.json();
        setCustomer(data);
        setFormData({
          name: data.name || "",
          email: data.email || "",
          phone: data.phone || "",
          address: data.address || "",
          city: data.city || "",
        });
      } catch (error) {
        console.error("Error fetching customer:", error);
        setLocation("/");
      } finally {
        setIsLoading(false);
      }
    };

    fetchCustomer();
  }, [setLocation]);

  const adminLoginMutation = useMutation({
    mutationFn: async () => {
      const res = await apiRequest("POST", "/api/admin/login", {
        username: adminUsername,
        password: adminPassword,
      });
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/auth-status"] });
      queryClient.refetchQueries({ queryKey: ["/api/admin/auth-status"] });

      toast({
        title: "تم التسجيل بنجاح",
        description: "مرحبا بك في لوحة التحكم",
      });

      setTimeout(() => {
        setLocation("/admin");
      }, 300);
    },
    onError: () => {
      toast({
        title: "خطأ في التسجيل",
        description: "اسم المستخدم أو كلمة المرور غير صحيحة",
        variant: "destructive",
      });
    },
  });

  const handleLogoutCustomer = () => {
    localStorage.removeItem("customerToken");
    localStorage.removeItem("customerId");
    localStorage.removeItem("customerEmail");
    localStorage.removeItem("customerName");
    setLocation("/");
  };

  const handleSave = () => {
    setIsEditing(false);
  };

  const handleAdminLogin = (e: React.FormEvent) => {
    e.preventDefault();
    if (!adminUsername || !adminPassword) {
      toast({
        title: "حقول مطلوبة",
        description: "يرجى إدخال اسم المستخدم وكلمة المرور",
        variant: "destructive",
      });
      return;
    }
    adminLoginMutation.mutate();
  };

  if (!customer && !isLoading) {
    return null;
  }

  return (
    <div className="min-h-screen flex flex-col">
      <Header />

      <main className="flex-1 container mx-auto px-4 py-8">
        <div className="max-w-2xl mx-auto">
          <div className="mb-8">
            <h1 className="text-3xl font-bold mb-2" data-testid="text-account-title">
              الحساب والإدارة
            </h1>
            <p className="text-muted-foreground">إدارة حسابك الشخصي أو دخول لوحة التحكم</p>
          </div>

          <Tabs defaultValue="account" className="w-full" dir="rtl">
            <TabsList className="grid w-full grid-cols-2" data-testid="tabs-account-admin">
              <TabsTrigger value="account" data-testid="tab-account">
                معلومات حسابي
              </TabsTrigger>
              <TabsTrigger value="admin" data-testid="tab-admin">
                تسجيل دخول Admin
              </TabsTrigger>
            </TabsList>

            {/* Customer Account Tab */}
            <TabsContent value="account" className="space-y-6 mt-6">
              {isLoading ? (
                <div className="space-y-4">
                  <Skeleton className="h-16" />
                  <Skeleton className="h-16" />
                  <Skeleton className="h-16" />
                </div>
              ) : (
                <div className="space-y-6">
                  <Card className="hover-elevate">
                    <CardHeader className="flex flex-row items-center justify-between">
                      <CardTitle className="flex items-center gap-2" data-testid="text-account-info">
                        <User className="h-5 w-5" />
                        معلومات الحساب
                      </CardTitle>
                      {!isEditing && (
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => setIsEditing(true)}
                          data-testid="button-edit-account"
                        >
                          تعديل
                        </Button>
                      )}
                    </CardHeader>
                    <CardContent className="space-y-4">
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        {/* Name */}
                        <div className="space-y-2">
                          <Label className="flex items-center gap-2">
                            <User className="h-4 w-4" />
                            الاسم
                          </Label>
                          {isEditing ? (
                            <Input
                              value={formData.name}
                              onChange={(e) =>
                                setFormData((prev) => ({ ...prev, name: e.target.value }))
                              }
                              data-testid="input-name"
                            />
                          ) : (
                            <p className="text-sm font-medium" data-testid="text-name">
                              {customer?.name || "-"}
                            </p>
                          )}
                        </div>

                        {/* Email */}
                        <div className="space-y-2">
                          <Label className="flex items-center gap-2">
                            <Mail className="h-4 w-4" />
                            البريد الإلكتروني
                          </Label>
                          {isEditing ? (
                            <Input
                              type="email"
                              value={formData.email}
                              disabled
                              dir="ltr"
                              data-testid="input-email"
                            />
                          ) : (
                            <p className="text-sm font-medium dir-ltr" data-testid="text-email">
                              {customer?.email || "-"}
                            </p>
                          )}
                        </div>

                        {/* Phone */}
                        <div className="space-y-2">
                          <Label className="flex items-center gap-2">
                            <Phone className="h-4 w-4" />
                            رقم الهاتف
                          </Label>
                          {isEditing ? (
                            <Input
                              value={formData.phone}
                              onChange={(e) =>
                                setFormData((prev) => ({ ...prev, phone: e.target.value }))
                              }
                              dir="ltr"
                              data-testid="input-phone"
                            />
                          ) : (
                            <p className="text-sm font-medium dir-ltr" data-testid="text-phone">
                              {customer?.phone || "-"}
                            </p>
                          )}
                        </div>

                        {/* City */}
                        <div className="space-y-2">
                          <Label className="flex items-center gap-2">
                            <MapPin className="h-4 w-4" />
                            المدينة
                          </Label>
                          {isEditing ? (
                            <Input
                              value={formData.city}
                              onChange={(e) =>
                                setFormData((prev) => ({ ...prev, city: e.target.value }))
                              }
                              data-testid="input-city"
                            />
                          ) : (
                            <p className="text-sm font-medium" data-testid="text-city">
                              {customer?.city || "-"}
                            </p>
                          )}
                        </div>
                      </div>

                      {/* Address */}
                      <div className="space-y-2">
                        <Label className="flex items-center gap-2">
                          <MapPin className="h-4 w-4" />
                          العنوان
                        </Label>
                        {isEditing ? (
                          <Input
                            value={formData.address}
                            onChange={(e) =>
                              setFormData((prev) => ({ ...prev, address: e.target.value }))
                            }
                            data-testid="input-address"
                          />
                        ) : (
                          <p className="text-sm font-medium" data-testid="text-address">
                            {customer?.address || "-"}
                          </p>
                        )}
                      </div>

                      {isEditing && (
                        <div className="flex gap-2 pt-4 border-t">
                          <Button onClick={handleSave} data-testid="button-save">
                            حفظ
                          </Button>
                          <Button
                            variant="outline"
                            onClick={() => setIsEditing(false)}
                            data-testid="button-cancel"
                          >
                            إلغاء
                          </Button>
                        </div>
                      )}
                    </CardContent>
                  </Card>

                  {/* Logout Button */}
                  <div className="flex gap-2">
                    <Button
                      variant="destructive"
                      onClick={handleLogoutCustomer}
                      className="flex items-center gap-2"
                      data-testid="button-logout"
                    >
                      <LogOut className="h-4 w-4" />
                      تسجيل الخروج
                    </Button>
                  </div>
                </div>
              )}
            </TabsContent>

            {/* Admin Login Tab */}
            <TabsContent value="admin" className="mt-6">
              <Card className="hover-elevate">
                <CardHeader className="text-center space-y-4">
                  <div className="w-16 h-16 bg-primary rounded-xl flex items-center justify-center mx-auto">
                    <Lock className="h-8 w-8 text-primary-foreground" />
                  </div>
                  <CardTitle className="text-2xl" data-testid="text-admin-login-title">
                    تسجيل دخول الإدارة
                  </CardTitle>
                  <p className="text-muted-foreground text-sm">
                    أدخل بيانات اعتمادك للوصول إلى لوحة التحكم
                  </p>
                </CardHeader>

                <CardContent>
                  <form onSubmit={handleAdminLogin} className="space-y-4">
                    <div className="space-y-2">
                      <Label htmlFor="admin-username" data-testid="label-admin-username">
                        اسم المستخدم
                      </Label>
                      <Input
                        id="admin-username"
                        type="text"
                        value={adminUsername}
                        onChange={(e) => setAdminUsername(e.target.value)}
                        placeholder="أدخل اسم المستخدم"
                        data-testid="input-admin-username"
                        dir="rtl"
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="admin-password" data-testid="label-admin-password">
                        كلمة المرور
                      </Label>
                      <Input
                        id="admin-password"
                        type="password"
                        value={adminPassword}
                        onChange={(e) => setAdminPassword(e.target.value)}
                        placeholder="أدخل كلمة المرور"
                        data-testid="input-admin-password"
                        dir="rtl"
                      />
                    </div>

                    <Button
                      type="submit"
                      className="w-full flex items-center gap-2"
                      disabled={adminLoginMutation.isPending}
                      data-testid="button-admin-login"
                    >
                      {adminLoginMutation.isPending ? (
                        <>
                          <Loader2 className="h-4 w-4 animate-spin" />
                          جاري التسجيل...
                        </>
                      ) : (
                        <>
                          <LogIn className="h-4 w-4" />
                          تسجيل الدخول
                        </>
                      )}
                    </Button>
                  </form>
                </CardContent>
              </Card>
            </TabsContent>
          </Tabs>
        </div>
      </main>

      <Footer />
    </div>
  );
}

client/src/components/ui/menubar.tsx.content:
"use client"

import * as React from "react"
import * as MenubarPrimitive from "@radix-ui/react-menubar"
import { Check, ChevronRight, Circle } from "lucide-react"

import { cn } from "@/lib/utils"

function MenubarMenu({
  ...props
}: React.ComponentProps<typeof MenubarPrimitive.Menu>) {
  return <MenubarPrimitive.Menu {...props} />
}

function MenubarGroup({
  ...props
}: React.ComponentProps<typeof MenubarPrimitive.Group>) {
  return <MenubarPrimitive.Group {...props} />
}

function MenubarPortal({
  ...props
}: React.ComponentProps<typeof MenubarPrimitive.Portal>) {
  return <MenubarPrimitive.Portal {...props} />
}

function MenubarRadioGroup({
  ...props
}: React.ComponentProps<typeof MenubarPrimitive.RadioGroup>) {
  return <MenubarPrimitive.RadioGroup {...props} />
}

function MenubarSub({
  ...props
}: React.ComponentProps<typeof MenubarPrimitive.Sub>) {
  return <MenubarPrimitive.Sub data-slot="menubar-sub" {...props} />
}

const Menubar = React.forwardRef<
  React.ElementRef<typeof MenubarPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof MenubarPrimitive.Root>
>(({ className, ...props }, ref) => (
  <MenubarPrimitive.Root
    ref={ref}
    className={cn(
      "flex h-10 items-center space-x-1 rounded-md border bg-background p-1",
      className
    )}
    {...props}
  />
))
Menubar.displayName = MenubarPrimitive.Root.displayName

const MenubarTrigger = React.forwardRef<
  React.ElementRef<typeof MenubarPrimitive.Trigger>,
  React.ComponentPropsWithoutRef<typeof MenubarPrimitive.Trigger>
>(({ className, ...props }, ref) => (
  <MenubarPrimitive.Trigger
    ref={ref}
    className={cn(
      "flex cursor-default select-none items-center rounded-sm px-3 py-1.5 text-sm font-medium outline-none focus:bg-accent focus:text-accent-foreground data-[state=open]:bg-accent data-[state=open]:text-accent-foreground",
      className
    )}
    {...props}
  />
))
MenubarTrigger.displayName = MenubarPrimitive.Trigger.displayName

const MenubarSubTrigger = React.forwardRef<
  React.ElementRef<typeof MenubarPrimitive.SubTrigger>,
  React.ComponentPropsWithoutRef<typeof MenubarPrimitive.SubTrigger> & {
