import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { useMutation } from "@tanstack/react-query";
import { Header } from "@/components/storefront/Header";
import { Footer } from "@/components/storefront/Footer";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Separator } from "@/components/ui/separator";
import { useCart } from "@/context/CartContext";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Check, CreditCard, Banknote, Loader2 } from "lucide-react";

interface CheckoutForm {
  customerId?: number;
  name: string;
  email: string;
  phone: string;
  address: string;
  city: string;
  notes: string;
  paymentMethod: "cod" | "card";
}

export default function Checkout() {
  const [, setLocation] = useLocation();
  const { items, subtotal, clearCart } = useCart();
  const { toast } = useToast();

  // Check login status first to determine initial modal state
  const isAlreadyLoggedIn = !!localStorage.getItem("customerEmail");

  const [showAuthModal, setShowAuthModal] = useState(!isAlreadyLoggedIn);
  const [form, setForm] = useState<CheckoutForm>({
    name: "",
    email: "",
    phone: "",
    address: "",
    city: "",
    notes: "",
    paymentMethod: "cod",
  });

  const [step, setStep] = useState(1);
  const [isLoggedInCustomer, setIsLoggedInCustomer] = useState(isAlreadyLoggedIn);

  // Check if customer is already logged in and fetch their full profile
  useEffect(() => {
    const email = localStorage.getItem("customerEmail");
    const customerId = localStorage.getItem("customerId");
    const customerName = localStorage.getItem("customerName");

    if (email && customerId && customerName) {
      // Customer is already logged in
      setIsLoggedInCustomer(true);
      setShowAuthModal(false); // Immediately hide modal for logged-in customers

      fetch(`/api/customers/by-email?email=${encodeURIComponent(email)}`)
        .then((res) => res.json())
        .then((customer) => {
          setForm((prev) => ({
            ...prev,
            customerId: parseInt(customerId),
            email: customer.email || email,
            name: customer.name || customerName,
            phone: customer.phone || "",
            address: customer.address || "",
            city: customer.city || "",
          }));
        })
        .catch((error) => {
          console.error("Error fetching customer profile:", error);
          setForm((prev) => ({
            ...prev,
            customerId: parseInt(customerId),
            email,
            name: customerName,
          }));
        });
    }
  }, []);

  const shippingCost = subtotal > 200 ? 0 : 25;
  const total = subtotal + shippingCost;

  const createOrderMutation = useMutation({
    mutationFn: async () => {
      const orderData = {
        customerId: form.customerId || null,
        customerName: form.name,
        customerEmail: form.email,
        customerPhone: form.phone,
        shippingAddress: form.address,
        shippingCity: form.city,
        notes: form.notes,
        paymentMethod: form.paymentMethod,
        subtotal: subtotal.toString(),
        shippingCost: shippingCost.toString(),
        total: total.toString(),
        items: items.map((item) => ({
          productId: item.productId,
          productNameAr: item.product.nameAr,
          quantity: item.quantity,
          price: item.product.price,
          total: (parseFloat(item.product.price) * item.quantity).toString(),
        })),
      };
      const response = await apiRequest("POST", "/api/orders", orderData);
      return await response.json() as { orderNumber: string };
    },
    onSuccess: (data) => {
      clearCart();
      setLocation(`/order-confirmation/${data.orderNumber}`);
    },
    onError: () => {
      toast({
        title: "حدث خطأ",
        description: "لم نتمكن من إتمام طلبك. يرجى المحاولة مرة أخرى.",
        variant: "destructive",
      });
    },
  });

  const handleChange = (field: keyof CheckoutForm, value: string) => {
    setForm((prev) => ({ ...prev, [field]: value }));
  };

  const validateStep1 = () => {
    if (!form.name || !form.email || !form.phone || !form.address || !form.city) {
      toast({
        title: "يرجى ملء جميع الحقول المطلوبة",
        variant: "destructive",
      });
      return false;
    }
    return true;
  };

  const handleNextStep = () => {
    if (validateStep1()) {
      setStep(2);
    }
  };

  const handleSubmit = () => {
    createOrderMutation.mutate();
  };


  if (items.length === 0) {
    setLocation("/cart");
    return null;
  }

  return (
    <div className="min-h-screen flex flex-col">
      <Header />

      <main className="flex-1 container mx-auto px-4 py-8">
        <div className="max-w-4xl mx-auto">
          <h1 className="text-2xl font-bold mb-8" data-testid="text-checkout-title">إتمام الطلب</h1>

          <div className="flex items-center justify-center mb-8">
            <div className="flex items-center gap-2">
              <div
                className={`w-10 h-10 rounded-full flex items-center justify-center ${
                  step >= 1 ? "bg-primary text-primary-foreground" : "bg-muted"
                }`}
              >
                {step > 1 ? <Check className="h-5 w-5" /> : "1"}
              </div>
              <span className={step >= 1 ? "font-medium" : "text-muted-foreground"}>
                معلومات الشحن
              </span>
            </div>
            <div className="w-16 h-0.5 bg-muted mx-2" />
            <div className="flex items-center gap-2">
              <div
                className={`w-10 h-10 rounded-full flex items-center justify-center ${
                  step >= 2 ? "bg-primary text-primary-foreground" : "bg-muted"
                }`}
              >
                {step > 2 ? <Check className="h-5 w-5" /> : "2"}
              </div>
              <span className={step >= 2 ? "font-medium" : "text-muted-foreground"}>
                الدفع والتأكيد
              </span>
            </div>
          </div>

          <div className="grid lg:grid-cols-3 gap-8">
            <div className="lg:col-span-2">
              {step === 1 && (
                <Card>
                  <CardHeader>
                    <CardTitle>معلومات الشحن</CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    <div className="grid sm:grid-cols-2 gap-4">
                      <div className="space-y-2">
                        <Label htmlFor="name">الاسم الكامل *</Label>
                        <Input
                          id="name"
                          value={form.name}
                          onChange={(e) => handleChange("name", e.target.value)}
                          placeholder="أدخل اسمك الكامل"
                          disabled={isLoggedInCustomer}
                          data-testid="input-name"
                        />
                      </div>
                      <div className="space-y-2">
                        <Label htmlFor="email">البريد الإلكتروني *</Label>
                        <Input
                          id="email"
                          type="email"
                          value={form.email}
                          onChange={(e) => handleChange("email", e.target.value)}
                          placeholder="example@email.com"
                          dir="ltr"
                          disabled={isLoggedInCustomer}
                          data-testid="input-email"
                        />
                      </div>
                    </div>
                    <div className="grid sm:grid-cols-2 gap-4">
                      <div className="space-y-2">
                        <Label htmlFor="phone">رقم الهاتف *</Label>
                        <Input
                          id="phone"
                          type="tel"
                          value={form.phone}
                          onChange={(e) => handleChange("phone", e.target.value)}
                          placeholder="+966 5x xxx xxxx"
                          dir="ltr"
                          disabled={isLoggedInCustomer}
                          data-testid="input-phone"
                        />
                      </div>
                      <div className="space-y-2">
                        <Label htmlFor="city">المدينة *</Label>
                        <Input
                          id="city"
                          value={form.city}
                          onChange={(e) => handleChange("city", e.target.value)}
                          placeholder="الرياض"
                          disabled={isLoggedInCustomer}
                          data-testid="input-city"
                        />
                      </div>
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="address">العنوان الكامل *</Label>
                      <Textarea
                        id="address"
                        value={form.address}
                        onChange={(e) => handleChange("address", e.target.value)}
                        placeholder="الحي، الشارع، رقم المبنى"
                        disabled={isLoggedInCustomer}
                        data-testid="input-address"
                      />
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="notes">ملاحظات إضافية</Label>
                      <Textarea
                        id="notes"
                        value={form.notes}
                        onChange={(e) => handleChange("notes", e.target.value)}
                        placeholder="ملاحظات للتوصيل (اختياري)"
                        data-testid="input-notes"
                      />
                    </div>
                    <Button onClick={handleNextStep} className="w-full" data-testid="button-next-step">
                      التالي: الدفع
                    </Button>
                  </CardContent>
                </Card>
              )}

              {step === 2 && (
                <Card>
                  <CardHeader>
                    <CardTitle>طريقة الدفع</CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-6">
                    <RadioGroup
                      value={form.paymentMethod}
                      onValueChange={(value) => handleChange("paymentMethod", value)}
                      className="space-y-3"
                    >
                      <div
                        className={`flex items-center gap-4 p-4 border rounded-lg cursor-pointer hover-elevate ${
                          form.paymentMethod === "cod" ? "border-primary bg-primary/5" : ""
                        }`}
                        onClick={() => handleChange("paymentMethod", "cod")}
                      >
                        <RadioGroupItem value="cod" id="cod" data-testid="radio-cod" />
                        <Banknote className="h-6 w-6 text-primary" />
                        <div>
                          <Label htmlFor="cod" className="font-medium cursor-pointer">
                            الدفع عند الاستلام
                          </Label>
                          <p className="text-sm text-muted-foreground">
                            ادفع نقداً عند استلام طلبك
                          </p>
                        </div>
                      </div>
                      <div
                        className={`flex items-center gap-4 p-4 border rounded-lg cursor-pointer hover-elevate ${
                          form.paymentMethod === "card" ? "border-primary bg-primary/5" : ""
                        }`}
                        onClick={() => handleChange("paymentMethod", "card")}
                      >
                        <RadioGroupItem value="card" id="card" data-testid="radio-card" />
                        <CreditCard className="h-6 w-6 text-primary" />
                        <div>
                          <Label htmlFor="card" className="font-medium cursor-pointer">
                            بطاقة ائتمان
                          </Label>
                          <p className="text-sm text-muted-foreground">
                            Visa, Mastercard, مدى
                          </p>
                        </div>
                      </div>
                    </RadioGroup>

                    {form.paymentMethod === "card" && (
                      <div className="p-4 bg-muted rounded-lg text-center">
                        <p className="text-muted-foreground">
                          سيتم توجيهك لبوابة الدفع الآمنة بعد تأكيد الطلب
                        </p>
                      </div>
                    )}

                    <div className="flex gap-4">
                      <Button variant="outline" onClick={() => setStep(1)} className="flex-1">
                        السابق
                      </Button>
                      <Button
                        onClick={handleSubmit}
                        disabled={createOrderMutation.isPending}
                        className="flex-1"
                        data-testid="button-confirm-order"
                      >
                        {createOrderMutation.isPending ? (
                          <>
                            <Loader2 className="h-4 w-4 animate-spin ml-2" />
                            جاري التأكيد...
                          </>
                        ) : (
                          "تأكيد الطلب"
                        )}
                      </Button>
                    </div>
                  </CardContent>
                </Card>
              )}
            </div>

            <div className="lg:col-span-1">
              <Card className="sticky top-24">
                <CardHeader>
                  <CardTitle>ملخص الطلب</CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="space-y-3 max-h-48 overflow-y-auto">
                    {items.map((item) => (
                      <div key={item.productId} className="flex gap-3">
                        <div className="w-16 h-16 rounded-md overflow-hidden bg-muted flex-shrink-0">
                          <img
                            src={item.product.images?.[0] || "https://placehold.co/64x64/e2e8f0/64748b?text=صورة"}
                            alt={item.product.nameAr}
                            className="w-full h-full object-cover"
                          />
                        </div>
                        <div className="flex-1 min-w-0">
                          <p className="text-sm font-medium line-clamp-1">{item.product.nameAr}</p>
                          <p className="text-sm text-muted-foreground">
                            {item.quantity} x {parseFloat(item.product.price).toFixed(2)} ر.س
                          </p>
                        </div>
                      </div>
                    ))}
                  </div>
                  <Separator />
                  <div className="flex justify-between text-sm">
                    <span className="text-muted-foreground">المجموع الفرعي</span>
                    <span>{subtotal.toFixed(2)} ر.س</span>
                  </div>
                  <div className="flex justify-between text-sm">
                    <span className="text-muted-foreground">الشحن</span>
                    <span>
                      {shippingCost === 0 ? (
                        <span className="text-green-600">مجاني</span>
                      ) : (
                        `${shippingCost.toFixed(2)} ر.س`
                      )}
                    </span>
                  </div>
                  <Separator />
                  <div className="flex justify-between font-bold text-lg">
                    <span>الإجمالي</span>
                    <span className="text-primary">{total.toFixed(2)} ر.س</span>
                  </div>
                </CardContent>
              </Card>
            </div>
          </div>
        </div>
      </main>

      <Footer />
    </div>
  );
}

server/routes.ts.content:
import type { Express } from "express";
import { type Server } from "http";
import { storage } from "./storage";
import { setupAdminAuth, isAdminAuthenticated } from "./adminAuth";
import { insertCategorySchema, insertProductSchema, insertOrderSchema, insertOrderItemSchema, customerLoginSchema, customerRegisterSchema } from "@shared/schema";
import { z } from "zod";
import crypto from "crypto";

function generateOrderNumber(): string {
  const timestamp = Date.now().toString(36).toUpperCase();
  const random = Math.random().toString(36).substring(2, 6).toUpperCase();
  return `ORD-${timestamp}-${random}`;
}

function hashPassword(password: string): string {
  return crypto.createHash("sha256").update(password + "salt").digest("hex");
}

function generateToken(): string {
  return crypto.randomBytes(32).toString("hex");
}

export async function registerRoutes(httpServer: Server, app: Express): Promise<Server> {
  // Admin auth setup
  setupAdminAuth(app);

  // ==================== CATEGORY ROUTES ====================

  // Get all categories
  app.get("/api/categories", async (req, res) => {
    try {
      const categories = await storage.getCategories();
      res.json(categories);
    } catch (error) {
      console.error("Error fetching categories:", error);
      res.status(500).json({ message: "Failed to fetch categories" });
    }
  });

  // Get single category
  app.get("/api/categories/:id", async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const category = await storage.getCategory(id);
      if (!category) {
        return res.status(404).json({ message: "Category not found" });
      }
      res.json(category);
    } catch (error) {
      console.error("Error fetching category:", error);
      res.status(500).json({ message: "Failed to fetch category" });
    }
  });

  // Create category (admin only)
  app.post("/api/categories", isAdminAuthenticated, async (req, res) => {
    try {
      const validatedData = insertCategorySchema.parse(req.body);
      const category = await storage.createCategory(validatedData);
      res.status(201).json(category);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid data", errors: error.errors });
      }
      console.error("Error creating category:", error);
      res.status(500).json({ message: "Failed to create category" });
    }
  });

  // Update category (admin only)
  app.patch("/api/categories/:id", isAdminAuthenticated, async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const validatedData = insertCategorySchema.partial().parse(req.body);
      const category = await storage.updateCategory(id, validatedData);
      if (!category) {
        return res.status(404).json({ message: "Category not found" });
      }
      res.json(category);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid data", errors: error.errors });
      }
      console.error("Error updating category:", error);
      res.status(500).json({ message: "Failed to update category" });
    }
  });

  // Delete category (admin only)
  app.delete("/api/categories/:id", isAdminAuthenticated, async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      await storage.deleteCategory(id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting category:", error);
      res.status(500).json({ message: "Failed to delete category" });
    }
  });

  // ==================== PRODUCT ROUTES ====================

  // Get all products
  app.get("/api/products", async (req, res) => {
    try {
      const products = await storage.getProducts();
      res.json(products);
    } catch (error) {
      console.error("Error fetching products:", error);
      res.status(500).json({ message: "Failed to fetch products" });
    }
  });

  // Get single product
  app.get("/api/products/:id", async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const product = await storage.getProduct(id);
      if (!product) {
        return res.status(404).json({ message: "Product not found" });
      }
      res.json(product);
    } catch (error) {
      console.error("Error fetching product:", error);
      res.status(500).json({ message: "Failed to fetch product" });
    }
  });

  // Create product (admin only)
  app.post("/api/products", isAdminAuthenticated, async (req, res) => {
    try {
      const validatedData = insertProductSchema.parse(req.body);
      const product = await storage.createProduct(validatedData);
      res.status(201).json(product);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid data", errors: error.errors });
      }
      console.error("Error creating product:", error);
      res.status(500).json({ message: "Failed to create product" });
    }
  });

  // Update product (admin only)
  app.patch("/api/products/:id", isAdminAuthenticated, async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const validatedData = insertProductSchema.partial().parse(req.body);
      const product = await storage.updateProduct(id, validatedData);
      if (!product) {
        return res.status(404).json({ message: "Product not found" });
      }
      res.json(product);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid data", errors: error.errors });
      }
      console.error("Error updating product:", error);
      res.status(500).json({ message: "Failed to update product" });
    }
  });

  // Delete product (admin only)
  app.delete("/api/products/:id", isAdminAuthenticated, async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      await storage.deleteProduct(id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting product:", error);
      res.status(500).json({ message: "Failed to delete product" });
    }
  });

  // ==================== CUSTOMER ROUTES ====================

  // Get all customers (admin only)
  app.get("/api/customers", isAdminAuthenticated, async (req, res) => {
    try {
      const customers = await storage.getCustomers();
      res.json(customers);
    } catch (error) {
      console.error("Error fetching customers:", error);
      res.status(500).json({ message: "Failed to fetch customers" });
    }
  });

  // Get customer by email (for customers)
  app.get("/api/customers/by-email", async (req, res) => {
    try {
      const email = req.query.email as string;
      if (!email) {
        return res.status(400).json({ message: "Email is required" });
      }
      const customer = await storage.getCustomerByEmail(email);
      if (!customer) {
        return res.status(404).json({ message: "Customer not found" });
      }
      res.json(customer);
    } catch (error) {
      console.error("Error fetching customer:", error);
      res.status(500).json({ message: "Failed to fetch customer" });
    }
  });

  // Customer login
  app.post("/api/customers/login", async (req, res) => {
    try {
      const { email, password } = customerLoginSchema.parse(req.body);
      const customer = await storage.getCustomerByEmail(email);

      if (!customer || !customer.password || customer.password !== hashPassword(password)) {
        return res.status(401).json({ message: "البريد الإلكتروني أو كلمة المرور غير صحيحة" });
      }

      const token = generateToken();
      res.json({
        token,
        customerId: customer.id,
        customerName: customer.name,
        customerEmail: customer.email,
      });
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid data", errors: error.errors });
      }
      console.error("Error logging in customer:", error);
      res.status(500).json({ message: "Failed to login" });
    }
  });

  // Customer register
  app.post("/api/customers/register", async (req, res) => {
    try {
      const { name, email, password } = customerRegisterSchema.parse(req.body);

      // Check if customer already exists
      const existingCustomer = await storage.getCustomerByEmail(email);
      if (existingCustomer) {
        return res.status(409).json({ message: "هذا البريد الإلكتروني مسجل بالفعل" });
      }

      // Create new customer
      const customer = await storage.createCustomer({
        name,
        email,
        password: hashPassword(password),
      });

      const token = generateToken();
      res.status(201).json({
        token,
        customerId: customer.id,
        customerName: customer.name,
        customerEmail: customer.email,
      });
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid data", errors: error.errors });
      }
      console.error("Error registering customer:", error);
      res.status(500).json({ message: "Failed to register" });
    }
  });

  // ==================== ORDER ROUTES ====================

  // Get all orders (admin only)
  app.get("/api/orders", isAdminAuthenticated, async (req, res) => {
    try {
      const orders = await storage.getOrders();
      res.json(orders);
    } catch (error) {
      console.error("Error fetching orders:", error);
      res.status(500).json({ message: "Failed to fetch orders" });
    }
  });

  // Get order by order number (for customers) - MUST be before :id route
  app.get("/api/orders/number/:orderNumber", async (req, res) => {
    try {
      const order = await storage.getOrderByNumber(req.params.orderNumber);
      if (!order) {
        return res.status(404).json({ message: "Order not found" });
      }
      res.json(order);
    } catch (error) {
      console.error("Error fetching order:", error);
      res.status(500).json({ message: "Failed to fetch order" });
    }
  });

  // Get orders by customer email (for customers) - MUST be before :id route
  app.get("/api/orders/by-email", async (req, res) => {
    try {
      const email = req.query.email as string;
      if (!email) {
        return res.status(400).json({ message: "Email is required" });
      }
      const orders = await storage.getOrdersByEmail(email);
      res.json(orders);
    } catch (error) {
      console.error("Error fetching orders by email:", error);
      res.status(500).json({ message: "Failed to fetch orders" });
    }
  });

  // Get single order by ID (admin only)
  app.get("/api/orders/:id", isAdminAuthenticated, async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      if (isNaN(id)) {
        return res.status(400).json({ message: "Invalid order ID" });
      }
      const order = await storage.getOrder(id);
      if (!order) {
        return res.status(404).json({ message: "Order not found" });
      }
      res.json(order);
    } catch (error) {
      console.error("Error fetching order:", error);
      res.status(500).json({ message: "Failed to fetch order" });
    }
  });

  // Create order (public - for checkout)
  app.post("/api/orders", async (req, res) => {
    try {
      const { items, customerId, ...orderData } = req.body;

      // Use provided customerId or lookup by email
      let customer;
      if (customerId) {
        customer = await storage.getCustomer(customerId);
      } else {
        customer = await storage.getCustomerByEmail(orderData.customerEmail);
      }

      // If customer still doesn't exist, create one
      if (!customer) {
        customer = await storage.createCustomer({
          name: orderData.customerName,
          email: orderData.customerEmail,
          phone: orderData.customerPhone || null,
          address: orderData.shippingAddress,
          city: orderData.shippingCity || null,
        });
      }

      // Create order
      const orderNumber = generateOrderNumber();
      const newOrder = await storage.createOrder(
        {
          orderNumber,
          customerId: customer.id,
          customerName: orderData.customerName,
          customerEmail: orderData.customerEmail,
          customerPhone: orderData.customerPhone || null,
          shippingAddress: orderData.shippingAddress,
          shippingCity: orderData.shippingCity || null,
          subtotal: orderData.subtotal,
          shippingCost: orderData.shippingCost || "0",
          total: orderData.total,
          status: "pending",
          paymentMethod: orderData.paymentMethod || "cod",
          notes: orderData.notes || null,
        },
        items.map((item: any) => ({
          productId: item.productId,
          productNameAr: item.productNameAr,
          quantity: item.quantity,
          price: item.price,
          total: item.total,
        }))
      );

      res.status(201).json(newOrder);
    } catch (error) {
      console.error("Error creating order:", error);
      res.status(500).json({ message: "Failed to create order" });
    }
  });

  // Update order status (admin only)
  app.patch("/api/orders/:id/status", isAdminAuthenticated, async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const { status } = req.body;

      if (!status) {
        return res.status(400).json({ message: "Status is required" });
      }

      const order = await storage.updateOrderStatus(id, status);
      if (!order) {
        return res.status(404).json({ message: "Order not found" });
      }
      res.json(order);
    } catch (error) {
      console.error("Error updating order status:", error);
      res.status(500).json({ message: "Failed to update order status" });
    }
  });

  return httpServer;
}

