import { useQuery } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

export function useAdminAuth() {
  const { data, isLoading } = useQuery({
    queryKey: ["/api/admin/auth-status"],
    queryFn: async () => {
      const res = await apiRequest("GET", "/api/admin/auth-status", undefined);
      return await res.json() as { isAuthenticated: boolean };
    },
    retry: false,
  });

  return {
    isAuthenticated: data?.isAuthenticated || false,
    isLoading,
  };
}

client/src/hooks/useAdminLogout.ts.content:
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useLocation } from "wouter";

export function useAdminLogout() {
  const [, setLocation] = useLocation();
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async () => {
      const res = await apiRequest("POST", "/api/admin/logout", undefined);
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/auth-status"] });
      setLocation("/admin");
    },
  });
}

